import post from 'rj-lib/fetch/post';
import get from 'rj-lib/fetch/get';
import apiPath from './apiPath';

var findOrgByCurrentUser = function findOrgByCurrentUser() {
  return get(apiPath.findOrgByCurrentUser);
}; //获取当前用户所在机构的部门列表


var findTreeChildren = function findTreeChildren(data) {
  return get(apiPath.findTreeChildren + '/' + data);
}; //查询部门列表


var findUsers = function findUsers(data) {
  return get(apiPath.findUsers + '/' + data);
};

var findUserDTOPage = function findUserDTOPage(data) {
  return post(apiPath.findUserDTOPage + '?page=' + data.page + '&size=' + data.size, data);
};

var findOrgs = function findOrgs(data) {
  return get(apiPath.findOrgs + '/' + data);
}; //查找本地区/全省
